package practice;

public class TypeCastDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 30;
		
		double y;
		
		y = x;  
		
		System.out.println(x);
		System.out.println(y);
		
		///////////////////
		
		double r = 56.76;
		int t = (int)r;
		
		System.out.println(r);
		System.out.println(t);
			

	}

}
